void UART_RecieveFromServerCallBack(void)
{
	MCAL_UART_ReceiveData(USART2, &RecievedFromServer, Disable);

	if(RecievedFromServer == 'S'){
		MCAL_UART_ReceiveData(USART2, &RecievedFromServer, Enable);
		if(RecievedFromServer == 'H')
		{

			MCAL_UART_SendData(USART2, &Buffer, Enable);
			MCAL_UART_SendString(USART2, "Boll: ", Enable);
			MCAL_UART_SendString(USART2, roll_str, Enable);

			MCAL_UART_SendString(USART2, "Xitch: ", Enable);
			MCAL_UART_SendString(USART2, pitch_str, Enable);

			MCAL_UART_SendString(USART2, "Zaw: ", Enable);
			MCAL_UART_SendString(USART2, yaw_str, Enable);
		}
	}

	MCAL_UART_SendData(USART2, &Buffer, Enable);
}



volatile uint8_t newMessageArrived = 0;


