void UART_Test_Callback(void)
{
	MCAL_UART_ReceiveData(USART2, &RecievedFromServer, Enable);
	if(RecievedFromServer == PARKING_REQUEST || RecievedFromServer == RETRIEVAL_REQUEST)
		newMessageArrived = 1;

	MCAL_UART_SendData(USART2, &Buffer, Enable);
}

