void Clock_Init()
{
	MCAL_RCC_CLK_Init(HSE_CLK, RCC_CLK_72M);  // is a must due to MPU6050

	RCC_GPIOB_CLK_EN();
	RCC_GPIOA_CLK_EN();
	RCC_GPIOC_CLK_EN();

	RCC_USART2_CLK_EN();
	RCC_AFIO_CLK_EN();
	RCC_I2C1_CLK_EN();


	// Timer 3 for delay purposes
	RCC_TIMER3_CLK_EN();
	TIMER3_Init(RCC_CLK_36M);
}

